public class IPv6LinkLocalToIPv4Converter {
    public static void main(String[] args) {
        String ipv6LinkLocalAddress = "fe80::925:8fad:8de3:147b%rmnet_data0";
        String ipv4Address = convertIPv6LinkLocalToIPv4(ipv6LinkLocalAddress);
        System.out.println("IPv4 Address: " + ipv4Address);
    }

    public static String convertIPv6LinkLocalToIPv4(String ipv6LinkLocalAddress) {
        String ipv4Prefix = "169.254";

        int interfaceIdentifierStartIndex = ipv6LinkLocalAddress.indexOf('%');
        String interfaceIdentifier = ipv6LinkLocalAddress.substring(0, interfaceIdentifierStartIndex);

        String[] interfaceIdentifierParts = interfaceIdentifier.split(":");
        StringBuilder ipv4AddressBuilder = new StringBuilder(ipv4Prefix);
        for (int i = 6; i < 8; i++) {
            String hexPart = interfaceIdentifierParts[i];
            int decimalPart = Integer.parseInt(hexPart, 16);
            ipv4AddressBuilder.append(".").append(decimalPart);
        }

        return ipv4AddressBuilder.toString();
    }
