```java
import android.os.Bundle;
import android.util.Log;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.camera.core.Camera;
import androidx.camera.core.CameraSelector;
import androidx.camera.core.ImageCapture;
import androidx.camera.core.ImageCaptureException;
import androidx.camera.core.ImageProxy;
import androidx.camera.lifecycle.ProcessCameraProvider;
import androidx.camera.view.PreviewView;
import androidx.core.content.ContextCompat;
import androidx.lifecycle.LifecycleOwner;
import java.io.File;
import java.text.SimpleDateFormat;
import java.util.Locale;
import java.util.concurrent.Executor;
import java.util.concurrent.Executors;

public class MainActivity extends AppCompatActivity {

    private ImageCapture imageCapture;
    private PreviewView previewView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        previewView = findViewById(R.id.previewView);

        // Make sure to request camera permissions from the user (not shown here).

        // Initialize the ImageCapture object
        imageCapture = new ImageCapture.Builder().build();

        // Bind the camera to the lifecycle
        bindCamera();
    }

    private void bindCamera() {
        // Use ProcessCameraProvider to bind the camera to the lifecycle
        ProcessCameraProvider cameraProvider = null;
        try {
            cameraProvider = ProcessCameraProvider.getInstance(this);
        } catch (Exception e) {
            e.printStackTrace();
        }

        // Add a listener to start the camera when the lifecycle starts
        cameraProvider.addListener(() -> {
            try {
                CameraSelector cameraSelector = new CameraSelector.Builder()
                        .requireLensFacing(CameraSelector.LENS_FACING_BACK)
                        .build();

                Camera camera = cameraProvider.bindToLifecycle(
                        (LifecycleOwner) this,
                        cameraSelector,
                        imageCapture,
                        previewView.getPreview());
            } catch (Exception e) {
                e.printStackTrace();
            }
        }, ContextCompat.getMainExecutor(this));
    }

    // Method to capture the image
    private void captureImage() {
        // Create a file to store the image
        File outputFile = new File(getOutputDirectory(), getUniqueFileName() + ".jpg");

        ImageCapture.OutputFileOptions outputFileOptions =
                new ImageCapture.OutputFileOptions.Builder(outputFile).build();

        // Use a separate executor to perform the image capture
        Executor executor = Executors.newSingleThreadExecutor();

        // Call the takePicture method to capture the image
        imageCapture.takePicture(executor, new ImageCapture.OnImageCapturedCallback() {
            @Override
            public void onCaptureSuccess(@NonNull ImageProxy image) {
                // Image captured successfully, handle the image here
                // For example, you can display the captured image in an ImageView
                // or save it to a gallery.

                // Close the image to free up resources
                image.close();
            }

            @Override
            public void onError(@NonNull ImageCaptureException exception) {
                // Handle the error
                Log.e("ImageCapture", "Error capturing image: " + exception.getMessage());
            }
        });
    }

    // Helper method to get the output directory
    private File getOutputDirectory() {
        // You can customize this method to return the desired directory to save the images.
        // For example, you can use the app's cache directory or external storage.
        return getExternalMediaDirs()[0];
    }

    // Helper method to generate a unique filename for each captured image
    private String getUniqueFileName() {
        SimpleDateFormat sdf = new SimpleDateFormat("yyyyMMdd_HHmmss", Locale.US);
        return "IMG_" + sdf.format(System.currentTimeMillis());
    }
}
```

This code sets up the CameraX library, initializes the `ImageCapture` object, and captures an image when the `captureImage()` method is called. The captured image is saved in a file, and you can customize the `getOutputDirectory()` method to specify the location where you want to save the images.

