        override fun run() {
            val duration = exoPlayer.duration
            val currentPosition = exoPlayer.currentPosition

            // Calculate percentage viewed
            val percentageViewed = (currentPosition.toDouble() / duration.toDouble()) * 100

            if (percentageViewed >= 70) {
                // 70% of the video has been viewed
                // Add your logic here
                // ...
            }

            handler.postDelayed(this, progressInterval)
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        exoPlayer.release()
        handler.removeCallbacks(updateProgressRunnable)
    }
}
