```kotlin
class Node(var data: Int) {
    var next: Node? = null
}

class LinkedList {
    var head: Node? = null

    fun push(data: Int) {
        val newNode = Node(data)
        newNode.next = head
        head = newNode
    }

    fun printList() {
        var current = head
        while (current != null) {
            print("${current.data} ")
            current = current.next
        }
        println()
    }

    fun reverse() {
        var prev: Node? = null
        var current = head
        while (current != null) {
            val nextNode = current.next
            current.next = prev
            prev = current
            current = nextNode
        }
        head = prev
    }
}

